{"title":"Vega Visualisation Draft","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": "Event counts from all indexes",
  "data": {
    "url": {
      "index": "zeebe-payments",
      "body": {
        "aggs": {
          "terms": {"field": "processInstanceKey"},
          "categories": {
            "aggs": {
              "duration": {
                "scripted_metric": {
                  "init_script": "def startTime = doc['originDate'].value.getMillis();def stopTime = doc['timestamp'].value.getMillis();",
                  "map_script": "def time = stopTime-startTime",
                  "combine_script": "time",
                  "reduce_script": "return time;"
                }
              }
            }
          }
        },
        "size": 0,
        "query": {"bool": {"must": [], "filter": [], "should": []}}
      }
    },
    "format": {"property": "aggregations.categories.buckets"}
  },
  "mark": "bar",
  "encoding": {
    "x": {
      "field": "duration.value",
      "type": "temporal",
      "timeUnit": "seconds",
      "axis": {"title": "Time taken in seconds "}
    },
    "y": {"field": "key", "type": "nominal", "axis": {"title": "Tasks"}},
    "color": {"field": "key", "type": "nominal"}
  },
  "layer": [
    {
      "mark": "rect",
      "encoding": {
        "color": {
          "field": "duration.value",
          "type": "temporal",
          "timeUnit": "seconds",
          "title": "Time elapsed",
          "legend": {"direction": "horizontal", "gradientLength": 120}
        }
      }
    }
  ]
}"}}
